mapper.select:
SELECT cateno, name, cnt, rdate, seqno, visible 
FROM cate 
WHERE cateno=#{cateno}

mapper.update:
UPDATE cate 
SET name=#{name}, seqno=#{seqno} 
WHERE cateno=#{cateno}

UPDATE cate
SET seqno = seqno - 1
WHERE cateno = #{cateno}

UPDATE cate
SET seqno = seqno + 1
WHERE cateno = #{cateno}

UPDATE cate 
SET visible='Y'
WHERE cateno=#{cateno}

UPDATE cate 
SET visible='N'
WHERE cateno=#{cateno}

UPDATE cate 
SET cnt = cnt + 1 
WHERE cateno = #{cateno}

UPDATE cate 
SET cnt = cnt - 1 
WHERE cateno = #{cateno}
