mapper.select:
SELECT youtubeno, cosmeno, youtubetitle, youtubecontent, views, rdate, youtube, seqno, visible 
FROM cosme_youtube_tip
WHERE youtubeno = #{youtubeno}

SELECT COUNT(*) as cnt FROM cosme_youtube_tip
WHERE cosmeno=#{cosmeno}
WHERE cosmeno=#{cosmeno} AND (UPPER(youtubetitle) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(youtubecontent) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%')

SELECT youtubeno, cosmeno, youtubetitle, youtubecontent, views, rdate, youtube, seqno, visible, r FROM ( SELECT youtubeno, cosmeno, youtubetitle, youtubecontent, views, rdate, youtube, seqno, visible, rownum as r FROM ( SELECT youtubeno, cosmeno, youtubetitle, youtubecontent, views, rdate, youtube, seqno, visible FROM cosme_youtube_tip
WHERE cosmeno=#{cosmeno}
WHERE cosmeno=#{cosmeno} AND (UPPER(youtubetitle) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(youtubecontent) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%')
ORDER BY youtubeno DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

mapper.update:
UPDATE cosme_youtube_tip
SET youtubetitle=#{youtubetitle}, youtubecontent=#{youtubecontent}, youtube=#{youtube}
WHERE youtubeno=#{youtubeno}

mapper.select:
SELECT COUNT(*) as cnt 
FROM cosme_youtube_tip 
WHERE cosmeno=#{cosmeno}
