mapper.select:
SELECT fboardno, memberno, ftitle, fcontent, rdate, passwd, word, file1, file1saved, thumb1, size1, youtube, views, replycnt
FROM fboard
WHERE fboardno = #{fboardno}

mapper.update:
UPDATE fboard 
SET youtube=#{youtube} 
WHERE fboardno=#{fboardno}

mapper.select:
SELECT fboardno, memberno, ftitle, fcontent, rdate, file1, file1saved, thumb1, size1, youtube, views, r FROM ( SELECT fboardno, memberno, ftitle, fcontent, rdate, file1, file1saved, thumb1, size1, youtube, views, rownum as r FROM ( SELECT fboardno, memberno, ftitle, fcontent, rdate, file1, file1saved, thumb1, size1, youtube, views FROM fboard
WHERE UPPER(ftitle) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(fcontent) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%'
ORDER BY fboardno DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

SELECT COUNT(*) as cnt 
FROM fboard
WHERE fboardno = #{fboardno} AND passwd=#{passwd}

mapper.update:
UPDATE fboard
SET ftitle=#{ftitle}, fcontent=#{fcontent}, youtube=#{youtube} 
WHERE fboardno = #{fboardno}

UPDATE fboard
SET ftitle=#{ftitle}, fcontent=#{fcontent}
WHERE fboardno = #{fboardno}

UPDATE fboard
SET file1=#{file1}, file1saved=#{file1saved}, thumb1=#{thumb1}, size1=#{size1}
WHERE fboardno = #{fboardno}

UPDATE fboard 
SET views = views + 1 
WHERE fboardno = #{fboardno}

UPDATE fboard
SET replycnt = replycnt + 1
WHERE fboardno = #{fboardno}

UPDATE fboard
SET replycnt = replycnt - 1
WHERE fboardno = #{fboardno}
