mapper.select:
SELECT noticeno, adminno, ntitle, ncontent, passwd, rdate, file1, file1saved, thumb1, size1, views, youtube, word
FROM notice
WHERE noticeno = #{noticeno}

mapper.update:
UPDATE notice 
SET youtube=#{youtube} 
WHERE noticeno=#{noticeno}

mapper.select:
SELECT noticeno, adminno, ntitle, ncontent, rdate, file1, file1saved, thumb1, size1, youtube, views, r FROM ( SELECT noticeno, adminno, ntitle, ncontent, rdate, file1, file1saved, thumb1, size1, youtube, views, rownum as r FROM ( SELECT noticeno, adminno, ntitle, ncontent, rdate, file1, file1saved, thumb1, size1, youtube, views FROM notice
WHERE UPPER(ntitle) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(ncontent) LIKE '%' || UPPER(#{word}) || '%' OR UPPER(word) LIKE '%' || UPPER(#{word}) || '%'
ORDER BY noticeno DESC ) ) WHERE r >= #{start_num} AND r <= #{end_num}

SELECT COUNT(*) as cnt 
FROM notice 
WHERE adminno=#{adminno} AND passwd=#{passwd}

mapper.update:
UPDATE notice
SET ntitle=#{ntitle}, ncontent=#{ncontent}, youtube=#{youtube}
WHERE noticeno = #{noticeno}

UPDATE notice
SET file1=#{file1}, file1saved=#{file1saved}, thumb1=#{thumb1}, size1=#{size1}
WHERE noticeno = #{noticeno}

UPDATE notice 
SET views = views + 1 
WHERE noticeno = #{noticeno}
