mapper.select:
SELECT noticeno, adminno, ntitle, ncontent, passwd, rdate, file1, file1saved, thumb1, size1, views
FROM notice
WHERE noticeno = #{noticeno}

SELECT COUNT(*) as cnt 
FROM notice 
WHERE adminno=#{adminno} AND passwd=#{passwd}

mapper.update:
UPDATE notice
SET ntitle=#{ntitle}, ncontent=#{ncontent}
WHERE noticeno = #{noticeno}
